// Classe générant un graphe
var fs = require('fs');

/**
 * Classe générant un objet graphe à partir
 * du fichier passé en paramètre.
 **/
var Graphe = function(path) {

	var graphe = null;
	var data   = fs.readFileSync(path);

	try {
		graphe = JSON.parse(data);
	} catch(err) {
		console.log("ERREUR : " + err);
	}

	this.nbSommets = graphe.nbSommets;
	this.nbAretes  = graphe.nbAretes;
	this.aretes    = graphe.aretes;

};

Graphe.prototype = {

	toString: function() {
		return "Nombre de sommets : " + this.nbSommets + '\n' +
			"Nombre d'aretes   : " + this.nbAretes;
	},

	toJSON: function() {
		console.log(this);
		return JSON.stringify(this);
	}
};

module.exports = Graphe;