
// Algo Dijkstra
var Dijkstra = function(source, sommets, distance, graphe) {

	// Structure de donn√©es contenant les meilleurs chemins
	this.path = {
		source: source,
		sommets: sommets,
		distance: distance
	};

	var sourceCourante = source;
	var obj = graphe;
	
	for(var i = 0; i < this.path.sommets.length; i++) {
		sourceCourante = this.path.sommets[i];
		
		for (var j = 0; j < obj.edges.length; j++) {
		
			if (obj.edges[j].data.source === sourceCourante && obj.edges[j].data.target !== this.path.source) {

				var destination = parseInt(obj.edges[j].data.target)-2;
				var poids = obj.edges[j].data.weight + this.path.distance[sourceCourante-2];
				
				if 	( this.path.distance[destination] == 'x' || (this.path.distance[destination] > poids) ) {
					this.path.distance[destination] = poids;
				}
			}
		}
	}
};

module.exports = Dijkstra;