
// Algo Dijkstra
var Dijkstra = function(source, sommets, distance, graphe) {

	// Structure de donn√©es contenant les meilleurs chemins
	this.path = {
		source : source,
		sommets : sommets,
		distance: distance
	};

	var sourceCourante = source;
	var obj = graphe;
	
	for(var i = 0; i < this.path.sommets.lenght-2 ; i++) {
		sourceCourante = this.path.sommets[i];
		
		for (var j = 0; j < obj.edges.lenght; j++) {
		
			if (obj.edges[j].data.source == sourceCourante) {
			
				var destination = parseInt(obj.edges[j].data.target);
				
				if 	( this.path.distance[destination] == 'x' || 
					( this.path.distance[destination] > (obj.edges[j].data.weight+this.path.distance[destination]) ) ) {
					
					this.path.distance[destination] = this.path.distance[parseInt(sourceCourante)] + obj.edges[j].data.weight;
				}
			}
		}
	}
};
module.exports = Dijkstra;